appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        dataList: [],
        eventTypeList: [],
        pagesize: 20,
        pagenum: 1,
        startTime: null,
        endTime: null,
        currentPage: 1,
        type: ""
      };
    },
    mounted() {
      appsmith.onModelChange(model => {
        const data = JSON.parse(model.data);
        this.dataList = data.listData;
        this.eventTypeList = data.types;
        this.switchTheme(model.style);
      });
    },
    methods: {
      topage() {
        this.pagenum = this.currentPage;
        this.search();
      },
      lastpage() {
        this.pagenum -= 1;
        this.currentPage = this.pagenum;
        this.search();
      },
      nextpage() {
        this.pagenum += 1;
        this.currentPage = this.pagenum;
        this.search();
      },
      clear() {
        const start = document.getElementById("date_start");
        const end = document.getElementById("date_end");
        start.value = null;
        end.value = null;
        this.endTime = null;
        this.startTime = null;
      },
      startTimeOnChange(e) {
        this.startTime = e.target.value;
        console.log("startTimeOnChange", e.target.value);
      },
      endTimeOnChange(e) {
        this.endTime = e.target.value;
        console.log("endTimeOnChange", e.target.value);
      },
      quickSetTime(daynum) {
        const start = document.getElementById("date_start");
        const end = document.getElementById("date_end");
        switch (daynum) {
          case 1:
            this.endTime = moment().format("yyyy-MM-DD");
            this.startTime = moment().format("yyyy-MM-DD");
            break;
          case 7:
            this.endTime = moment().format("yyyy-MM-DD");
            this.startTime = moment().subtract(1, "weeks").format("yyyy-MM-DD");
            break;
          case 30:
            this.endTime = moment().format("yyyy-MM-DD");
            this.startTime = moment().subtract(1, "months").format("yyyy-MM-DD");
            break;
        }
        start.value = this.startTime;
        end.value = this.endTime;
        this.pagenum = 1;
        this.currentPage = 1;
        this.search();
      },
      search() {
        appsmith.triggerEvent("search", {
          type: this.type,
          pagesize: this.pagesize,
          pagenum: this.pagenum,
          startTime: this.startTime,
          endTime: this.endTime && this.endTime + " 23:59:59"
        });
      },
      switchTheme(theme) {
        var themeLink = document.getElementById('aux-style');
        themeLink.href = `https://test.superton.cn/aux/${theme}.css`;
        appsmith.triggerEvent("switchTheme");
      },
      chakan(data) {
        console.log('chakan', data);
        appsmith.updateModel({
          selectData: data
        });
        appsmith.triggerEvent('chakan');
      },
      typeChange(e) {
        this.type = e.target.selectedOptions[0].value;
        // appsmith.updateModel({type:this.type})
        appsmith.triggerEvent('onSelect', {
          type: this.type,
          pagesize: this.pagesize,
          pagenum: this.pagenum,
          startTime: this.startTime,
          endTime: this.endTime && this.endTime + " 23:59:59"
        });
      },
      close() {
        appsmith.triggerEvent("close");
      }
    }
  });
});

