@charset "UTF-8";
input[type=checkbox] {
  -webkit-appearance: none;
  border: 1px solid #999;
  outline: none;
  border-radius: 2px;
  border: 2px solid #007EFF;
  background: transparent;
  position: relative;
  cursor: pointer;
}

.check-input {
  width: 18px;
  height: 18px;
}

input.check-input[type=checkbox]:checked {
  background-color: #007EFF; /* 你可以更改这个颜色为你想要的颜色 */
}

input.check-input[type=checkbox]:checked::after {
  content: "";
  display: block;
  width: 14px;
  height: 14px;
  position: absolute;
  background-image: url("http://cdn.p.superton.cn/aizhiyan/check.svg");
  background-position: center;
  background-repeat: no-repeat;
  background-size: contain;
}

.riririr {
  position: fixed;
  top: 68px;
  right: 30px;
  color: red;
  z-index: 99;
  display: flex;
  gap: 10px;
}
.riririr .item {
  width: 24px;
  height: 24px;
  cursor: pointer;
}
.riririr .item:nth-child(1) {
  background-image: url("http://cdn.p.superton.cn/aizhiyan/video_select_play.png");
  background-size: 100% 100%;
}
.riririr .item:nth-child(2) {
  background-image: url("http://cdn.p.superton.cn/aizhiyan/video_select_cancel.png");
  background-size: 100% 100%;
}

.dianwei-list {
  overflow: none;
}

