appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        type: 0,
        listData: [],
        selectList: []
      };
    },
    mounted() {
      appsmith.onModelChange(model => {
        this.switchTheme(model.style);
        this.listData = JSON.parse(model.data);
      });
    },
    watch: {
      type(val) {
        this.selectList = [];
        this.listData = [];
        appsmith.triggerEvent("typeOnChange", {
          type: this.type
        });
      }
    },
    computed: {
      showCheckBox() {
        return function (item) {
          const index = this.selectList.findIndex(w => w.id == item.id);
          const a = index > -1 && this.selectList.length == 4;
          const b = this.selectList.length < 4;
          return a || b;
        };
      }
    },
    methods: {
      playvideos() {},
      cancelSelect(dom) {
        console.log("cancelSelect", dom);
        this.selectList = [];
      },
      checkbox(item, index) {
        const sindex = this.selectList.findIndex(w => w.id == item.id);
        if (sindex == -1) {
          this.selectList.push(item);
        } else {
          this.selectList.splice(sindex, 1);
        }
        console.log("checkbox", item, index, this.selectList);
      },
      enterDown(e) {},
      inputing() {},
      locationOnClick(data) {},
      showDialog() {},
      switchTheme(theme) {
        var themeLink = document.getElementById('aux-style');
        themeLink.href = `https://test.superton.cn/aux/${theme}.css`;
      }
    }
  });
});

