#app {
  height: calc(var(--appsmith-ui-height) * 1px);
  width: calc(var(--appsmith-ui-width) * 1px);
}

.streetai-expand-box-container {
  height: 100%;
  width: calc(100% - 10px);
  display: flex;
  flex-direction: column;
}
.streetai-expand-box-container .title-bg {
  height: 100%;
  text-indent: 20px;
  width: 100%;
  background-size: 100% 100%;
  font-size: 20px;
  color: white;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.streetai-expand-box-container .title-bg .title {
  font-family: "YouSheBiaoTiHei";
  position: relative;
}
.streetai-expand-box-container .title-bg .title::before {
  content: "";
  background: url("http://cdn.p.superton.cn/aizhiyan/expand_box_icon.png");
  width: 28px;
  height: 28px;
  position: absolute;
  left: -8px;
  top: 50%;
  transform: translateY(-50%);
}
.streetai-expand-box-container .title-bg .right {
  width: 18px;
  height: 18px;
  cursor: pointer;
}
.streetai-expand-box-container .content {
  flex: 1;
  height: 0;
  width: 100%;
}

