appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        title: appsmith.model.title,
        time: [],
        weekText: ["星期天", "星期一", "星期二", "星期三", "星期四", "星期五", "星期六"],
        timer: null,
        isHidden: false,
        isExpand: false,
        styleList: [{
          label: '科技蓝',
          value: 'aux'
        }, {
          label: '工业蓝',
          value: 'aux-style1'
        }, {
          label: '神秘黑',
          value: 'aux-style2'
        }]
      };
    },
    mounted() {
      this.setTime();
      document.title = this.title;
    },
    beforeDestroy() {
      clearInterval(this.timer);
    },
    methods: {
      typeChange(e) {
        console.log(e);
        appsmith.updateModel({
          style: e.target.value
        });
        appsmith.triggerEvent("styleChanged");
        this.switchTheme(e.target.value);
      },
      switchTheme(theme) {
        var themeLink = document.getElementById('aux-style');
        themeLink.href = `https://test.superton.cn/aux/${theme}.css`;
      },
      hiddenOnClick() {
        this.isHidden = !this.isHidden;
        this.$emit("hiddenOnClick", this.isHidden);
      },
      setTime() {
        if (this.timer) clearInterval(this.timer);
        this.timer = setInterval(() => {
          this.time = [moment().format("YYYY/MM/DD"), this.weekText[moment().format("e")], moment().format("HH:mm:ss")];
        }, 1000);
      }
    }
  });
});

