appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  var x_PI = 3.14159265358979324 * 3000.0 / 180.0;
  var PI = 3.1415926535897932384626;
  var a = 6378245.0;
  var ee = 0.00669342162296594323;
  new Vue({
    el: "#app",
    data() {
      return {
        type: 0,
        date: 30,
        yujingList: JSON.parse(appsmith.model.yujingList),
        shipinList: JSON.parse(appsmith.model.shipinList),
        map: null,
        markerCluster: null,
        timeRange: ["", ""],
        alarmData: null,
        eventShowTimer: null,
        getEventTimer: null,
        countdown: 8,
        selectLevel: null,
        currentLocationData: null,
        videoList: [],
        eventList: [],
        eventMarkersGroup: null,
        equipmentMarkersGroup: null
      };
    },
    mounted() {
      this.initMap();
      appsmith.onModelChange(model => {
        const videoData = JSON.parse(model.videoList == 'undefined' ? '[]' : model.videoList);
        const eventData = JSON.parse(model.eventList == 'undefined' ? '[]' : model.eventList);
        const alarmData = JSON.parse(model.alarmData);
        this.selectLevel = null;
        this.updateAlarmData(alarmData);
        if (!this.getEventTimer) {
          this.getEventTimer = setInterval(() => {
            appsmith.triggerEvent("getEvent");
          }, 1000 * 60 * 5);
        }
        this.setCurrentLocationData(model.currentLocationData);
        this.addVideoMarkers(videoData);
        this.addEventMarkers(eventData);
        this.switchTheme(model.style);
        console.log("markerCluster", this.markerCluster.getLayers());
        this.map.flyToBounds(this.markerCluster.getBounds());
      });
    },
    methods: {
      setCurrentLocationData(data) {
        if (data == "") {
          this.currentLocationData = null;
        } else {
          this.currentLocationData = JSON.parse(data);
        }
      },
      updateAlarmData(alarmData) {
        if (alarmData.length > 0) {
          this.alarmData = alarmData[0];
          this.countdown = 8;
          if (!this.eventShowTimer) {
            this.eventShowTimer = setInterval(() => {
              if (this.countdown < 1) {
                this.alarmData = null;
                clearInterval(this.eventShowTimer);
                this.eventShowTimer = null;
              } else {
                this.countdown -= 1;
              }
            }, 1000);
          }
        }
      },
      falseAlarm() {
        appsmith.triggerEvent("falseAlarm", {
          selectLevel: this.selectLevel,
          ...this.alarmData
        });
      },
      deleteEvent() {
        appsmith.triggerEvent("deleteEvent", {
          selectLevel: this.selectLevel,
          ...this.alarmData
        });
      },
      levelConfirm() {
        if (this.selectLevel) {
          appsmith.triggerEvent("levelConfirm", {
            selectLevel: this.selectLevel,
            ...this.alarmData
          });
        } else {
          appsmith.triggerEvent("message", {
            message: "请选择告警级别"
          });
        }
      },
      onSelect(e) {
        const value = e.target.value;
        switch (value) {
          case "一级":
            this.selectLevel = 1;
            break;
          case "二级":
            this.selectLevel = 2;
            break;
          case "三级":
            this.selectLevel = 3;
            break;
        }
        // appsmith.updateModel({selectLevel:this.selectLevel})	
      },
      switchTheme(theme) {
        var themeLink = document.getElementById('aux-style');
        themeLink.href = `https://test.superton.cn/aux/${theme}.css`;
      },
      onEventChecked(item, index) {
        this.yujingList = this.yujingList.map(data => {
          data.isSelected = data.title == item.title && data.isSelected == false;
          return data;
        });
        this.timeRange = this.yujingList[index].range;
        appsmith.updateModel({
          startTime: this.timeRange[0],
          endTime: this.timeRange[1]
        });
        appsmith.triggerEvent("dateOnChange");
      },
      onVideoChecked(item, index) {
        this.shipinList = this.shipinList.map(data => {
          data.isSelected = data.title == item.title && data.isSelected == false;
          return data;
        });
        console.log("onVideoChecked", index);
        // this.type = index
        appsmith.updateModel({
          type: index
        });
        appsmith.triggerEvent('videoTypeOnChange', {
          type: index
        });
      },
      gcj02ToWGS84(lng, lat) {
        var lat = +lat;
        var lng = +lng;
        if (this.out_of_china(lng, lat)) {
          return [lng, lat];
        } else {
          var dlat = this.transformlat(lng - 105.0, lat - 35.0);
          var dlng = this.transformlng(lng - 105.0, lat - 35.0);
          var radlat = lat / 180.0 * PI;
          var magic = Math.sin(radlat);
          magic = 1 - ee * magic * magic;
          var sqrtmagic = Math.sqrt(magic);
          dlat = dlat * 180.0 / (a * (1 - ee) / (magic * sqrtmagic) * PI);
          dlng = dlng * 180.0 / (a / sqrtmagic * Math.cos(radlat) * PI);
          var mglat = lat + dlat;
          var mglng = lng + dlng;
          return [lng * 2 - mglng, lat * 2 - mglat];
        }
        ;
      },
      addVideoMarkers(model) {
        if (!this.equipmentMarkersGroup) return;
        console.log("addVideoMarkers", model);
        this.videoList = model.filter(item => item.lonLat != null);
        this.equipmentMarkersGroup.clearLayers();
        if (this.videoList.length == 0 || !this.equipmentMarkersGroup) {
          return;
        }
        const list = this.videoList.map(item => {
          const lnglat = item.lonLat.split(',');
          console.log("lnglat", lnglat);
          const latlng = this.gcj02ToWGS84(lnglat[0], lnglat[1]);
          console.log("latlng", latlng);
          let name = "";
          if (item.state == 2) {
            name = "white_video_marker.png";
          } else {
            if (item.warningRecord) {
              name = "green_video_marker.png";
            } else {
              name = "red_video_marker.png";
            }
          }
          const marker = L.marker(latlng.reverse(), {
            icon: L.icon({
              iconUrl: `http://cdn.p.superton.cn/aizhiyan/${name}`,
              iconSize: [48, 48]
            })
          });
          marker.bindTooltip(item.name);
          marker.on("click", () => {
            console.log("点击 video marker", item);
            appsmith.triggerEvent("clickMarker", {
              data: item
            });
          });
          marker.addTo(this.equipmentMarkersGroup);
          return marker;
        });
        // console.log("addVideoMarkers",list)
        // this.equipmentMarkersGroup.addLayers(list);
      },
      addEventMarkers(model) {
        if (!this.eventMarkersGroup) return;
        this.eventList = model.filter(item => item.lonLat != null);
        console.log("addEventMarkers", this.eventList);
        this.eventMarkersGroup.clearLayers();
        if (this.eventList.length == 0 || !this.eventMarkersGroup) {
          return;
        }
        console.log("addEventMarkers", this.eventList);
        const list = this.eventList.map(item => {
          const lnglat = item.lonLat.split(',');
          const latlng = this.gcj02ToWGS84(lnglat[0], lnglat[1]);
          const marker = L.marker(latlng.reverse(), {
            icon: L.icon({
              iconUrl: 'http://cdn.p.superton.cn/aizhiyan/event_marker.png',
              iconSize: [48, 48]
            })
          });
          marker.bindTooltip(item.name);
          marker.on("click", () => {
            console.log("点击 event marker", item);
            appsmith.triggerEvent("clickMarker", {
              data: item
            });
          });
          marker.addTo(this.eventMarkersGroup);
          return marker;
        });
        // console.log("addEventMarkers",list)
        // this.equipmentMarkersGroup.addLayers(list);
      },
      initMap() {
        const layers = this.initLayer();
        this.map = L.map('map', {
          layers: layers,
          center: [29.832422913033476, 121.59343957901002],
          zoom: 16,
          maxZoom: 18,
          minZoom: 3,
          zoomControl: false,
          attributionControl: false
        });
        this.map.on('click', function (e) {
          console.log("map onclick", e);
        });
        this.markerCluster = L.markerClusterGroup();
        // this.map.addLayer(this.markerCluster)
        this.markerCluster.addTo(this.map);
        console.log('map', this.map);
        console.log('markerClusterGroup', this.markerCluster);
