appsmith.onReady(() => {
  new Vue({
    el: "#right-select-type",
    data() {
      return {
        loading: false,
        eventTypeList: [],
        listData: [],
        scrollOption: {
          step: 0.2
        },
        style: appsmith.model.style,
        timer: null
      };
    },
    mounted() {
      console.log("mountedmounted", appsmith.model);
      appsmith.triggerEvent('onMounted');
      appsmith.onModelChange(model => {
        console.log("onModelChange", model);
        if (model) {
          const dataModel = JSON.parse(model.data);
          this.listData = dataModel.listData;
          this.eventTypeList = dataModel.types;
          appsmith.triggerEvent('onModelChange', {
            model: model
          });
          this.switchTheme(model.style);
        }
      });
    },
    methods: {
      startCountdown() {
        if (this.timer) {
          clearInterval(this.timer);
          this.timer = null;
        }
        this.timer = setInterval(() => {
          appsmith.triggerEvent("get");
        }, 60 * 1000 * 5);
      },
      switchTheme(theme) {
        var themeLink = document.getElementById('aux-style');
        themeLink.href = `https://test.superton.cn/aux/${theme}.css`;
        appsmith.triggerEvent("switchTheme");
      },
      chakan(data) {
        console.log('chakan', data);
        appsmith.updateModel({
          selectData: data
        });
        appsmith.triggerEvent('chakan');
      },
      typeChange(e) {
        const selectType = e.target.selectedOptions[0];
        console.log('e', selectType.value);
        appsmith.updateModel({
          selectType: selectType.value
        });
        appsmith.triggerEvent('onSelect', {
          selectType: selectType.value
        });
      }
    }
  });
});

