@charset "UTF-8";
.videos-container {
  height: calc(var(--appsmith-ui-height) * 1px);
  width: calc(var(--appsmith-ui-width) * 1px);
  background: url("http://cdn.p.superton.cn/aizhiyan/new-video-bg.png");
  background-size: 100% 100%;
  background-color: transparent;
  color: white;
}
.videos-container > .content {
  display: flex;
  flex-direction: column;
  padding: 24px 200px;
  height: calc(100% - 48px);
  width: calc(100% - 400px);
}
.videos-container > .content .header {
  display: flex;
  padding: 4px 0;
  justify-content: space-between;
  position: relative;
}
.videos-container > .content .header i {
  width: 120%;
}
.videos-container > .content .header img {
  transform: translateY(-2px);
  cursor: pointer;
}
.videos-container > .content .header::after {
  content: "";
  position: absolute;
  width: 100%;
  height: 2px;
  background: #0572CC;
  bottom: 0;
}
.videos-container > .content .header .title {
  font-size: 24px;
  /* 为了兼容性，首先使用老版本的Webkit内核浏览器的语法 */
  background: -webkit-linear-gradient(#ffffff 10%, #77cef6);
  /* 然后是兼容Firefox的语法 */
  background: -moz-linear-gradient(#ffffff 10%, #77cef6);
  /* 接着是兼容Opear的语法 */
  background: -o-linear-gradient(#ffffff 10%, #77cef6);
  /* 最后是标准的语法 */
  background: linear-gradient(#ffffff 10%, #77cef6);
  /* 为了兼容旧版本的IE浏览器，可以使用渐变滤镜 */
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr="#ffffff", endColorstr="#77cef6");
  /* 以下是其他CSS属性，用于确保渐变效果 */
  -webkit-background-clip: text;
  -moz-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  -moz-text-fill-color: transparent;
  color: transparent;
}
.videos-container > .content .video-content {
  flex: 1;
  width: 100%;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  column-gap: 8px;
}
.videos-container > .content .video-content iframe {
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
}

