appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        videos: [],
        style: ""
      };
    },
    mounted() {
      appsmith.onModelChange(model => {
        this.switchTheme(model.style);
        this.videos = JSON.parse(model.videos);
      });
    },
    watch: {
      videos(val) {
        if (val.length == 1) {
          this.style = "grid-template-columns:1fr;";
        } else {
          this.style = "grid-template-columns:repeat(2,1fr);";
        }
      }
    },
    methods: {
      close() {
        appsmith.triggerEvent("close");
      },
      switchTheme(theme) {
        var themeLink = document.getElementById('aux-style');
        themeLink.href = `https://test.superton.cn/aux/${theme}.css`;
      }
    }
  });
});

